-- NVCB
DECLARE
  ho  SYS.ODCIVARCHAR2LIST := SYS.ODCIVARCHAR2LIST(
    'Nguyễn', 'Trần', 'Lê', 'Phạm', 'Hoàng', 'Võ', 'Đặng', 'Bùi', 'Đỗ', 'Hồ'
  );
  ten SYS.ODCIVARCHAR2LIST := SYS.ODCIVARCHAR2LIST(
    'An', 'Bình', 'Chi', 'Dũng', 'Hùng', 'Mai', 'Lan', 'Hương', 'Minh', 'Thảo', 'Nam', 'Khoa', 'Hải', 'Tâm', 'Thắng'
  );
BEGIN
  FOR i IN 300..799 LOOP
      MANV, HOTEN, PHAI, NGSINH, LUONG, PHUCAP, DT, VAITRO, MADV
    ) VALUES (
      'NV' || LPAD(i, 5, '0'),
      ho(MOD(i, ho.COUNT) + 1) || ' ' || ten(MOD(i, ten.COUNT) + 1),
      CASE MOD(i, 2) WHEN 0 THEN '1' ELSE '0' END,
      TO_DATE('1985-01-01', 'YYYY-MM-DD') + MOD(i, 7000),
      4000000 + MOD(i * 1111, 8000000),
      500000 + MOD(i * 2222, 3000000),
      '091' || TO_CHAR(1000000 + i),
      'NVCB',
      CASE 
        WHEN MOD(i, 6) = 0 THEN 'TOAN'
        WHEN MOD(i, 6) = 1 THEN 'CNTT'
        WHEN MOD(i, 6) = 2 THEN 'VATLY'
        WHEN MOD(i, 6) = 3 THEN 'SINH'
        WHEN MOD(i, 6) = 4 THEN 'HOA'
        ELSE 'KHKT'
      END
    );
  END LOOP;
  COMMIT;
END;
/
